import React, { useState } from 'react';
import Game from './components/Game';
import Result from './components/Result';
import Start from './components/Start';
import { game, result, start } from './utils/contants';

const  App =() => {
  const [state, setState] =useState('')
  const changePage = page => {
    setState({...state, page });
  }

  const changeName = name => {
    if (name) setState({...state, name });
  }

  const changeScore = score => {
    if(score[0] > score[1]){
      setState({...state,
        score: [state.score[0] + 1,state.score[1]],
        result: 'Lose'
      })
    }
    if(score[0] < score[1]){
      setState({...state,
        score: [state.score[0],state.score[1] + 1],
        result: 'Win'
      })
    }
    if(score[0] === score[1]){
      setState({...state, result: 'Draw'})
    }
  }


    switch (state.page) {
      case game:
        return <Game
                  nickName={state.name}
                  changePage={changePage}
                  getScore={changeScore}
                />;
      case result:
        return <Result 
                  changePage={changePage} 
                  score={state.score}
                  result={state.result}
                />
      default:
        return <Start 
                  name={state.name} 
                  changeName={changeName} 
                  changePage={changePage} 
                />
    }
}

export default App;
